            </div>
            <button type='button' className='w-full bg-blue-600 py-3 px-7 mt-3 text-white uppercase rounded text-sm font-medium shadow-md hover:bg-blue-700 transition duration-150 ease-in-out hover:shadow-lg active:bg-blue-800'>Sign in</button>
          </form>
        </div>
      </div>
    </section>
  )
}
