import React from 'react'
import { useLocation ,useNavigate} from 'react-router-dom';


export default function Header() {
 const location = useLocation();
 const navigate = useNavigate();
 console.log(navigate);
 function pathMatchRoute(route){
    if(route===location.pathname)
    return true;
 }
 console.log(location.pathname);
  return (
    <div className='bg-white border-b shadow-sm sticky top-0 z-50 h-10'>
        <header className='flex justify-between items-center px-3 max-w-6xl mx-auto'>
            <div>
                <h1 className='h-5 cursor-pointer text-2xl text-red-600' onClick={()=>navigate('/')}>Quara.com</h1>
            </div>
            <div >
                <ul className='flex space-x-10'>
                    <li className={`cursor-pointer py-3 text-sm font-semibold text-gray-400 border-b-[3px] border-b-transparent ${pathMatchRoute('/') && "text-black border-b-red-500"}`} onClick={()=>navigate('/')}>Home</li>
                    <li className={`cursor-pointer py-3 text-sm font-semibold text-gray-400 border-b-[3px] border-b-transparent ${pathMatchRoute('/offers') && "text-black border-b-red-500"}`} onClick={()=>navigate('/')}>Offers</li>
                    <li className={`cursor-pointer py-3 text-sm font-semibold text-gray-400 border-b-[3px] border-b-transparent ${pathMatchRoute('/sign-in') && "text-black border-b-red-500"}`}>SignIn</li>
                </ul>
            </div>
        </header>
    </div>
  )
}
